class DraculaTheme:
    from PyQt6.QtGui import QColor
    from core.config import cfg
    BACKGROUND = cfg.get_str('JanelaPrincipal', 'CorFundo', '#282a36')
    SIDEBAR = cfg.get_str('BlocoEsquerdo_Sidebar', 'CorFundo', '#21222c')
    CURRENT_LINE = '#44475a'
    FOREGROUND = '#f8f8f2'
    COMMENT = '#6272a4'
    CYAN = '#8be9fd'
    GREEN = '#50fa7b'
    ORANGE = '#ffb86c'
    PINK = '#ff79c6'
    PURPLE = '#bd93f9'
    RED = '#ff5555'
    YELLOW = '#f1fa8c'
    BORDER_RADIUS = cfg.get_str('EstiloGlobal', 'BorderRadius', '12px')
    FONT_FAMILY = cfg.get_str('EstiloGlobal', 'FonteFamilia', 'Segoe UI')
    INPUT_HEIGHT = cfg.get_str('BlocoCentral_Formularios', 'AlturaInput', '45') + 'px'
    INPUT_FONT_SIZE = cfg.get_str('BlocoCentral_Formularios', 'TamanhoFonteInput', '14px')
    BTN_GENERATE_HEIGHT = cfg.get_str('BlocoCentral_Formularios', 'AlturaBotaoGerar', '55') + 'px'
    BTN_GENERATE_FONT_SIZE = cfg.get_str('BlocoCentral_Formularios', 'TamanhoFonteBotao', '15px')
    BTN_ACTION_HEIGHT = cfg.get_str('BlocoDireito_Preview', 'AlturaBotoesAcao', '50') + 'px'
    STYLESHEET = f"\n    QMainWindow {{\n        background-color: {BACKGROUND};\n        color: {FOREGROUND};\n    }}\n    QWidget {{\n        background-color: {BACKGROUND};\n        color: {FOREGROUND};\n        font-family: '{FONT_FAMILY}', sans-serif;\n        font-size: 14px;\n    }}\n    \n    \n    QListWidget {{\n        background-color: {SIDEBAR};\n        border: none;\n        outline: none;\n    }}\n    QListWidget::item {{\n        height: 60px;\n        color: {COMMENT};\n        padding-left: 20px;\n        border-left: 4px solid transparent;\n        margin-bottom: 5px;\n    }}\n    QListWidget::item:selected {{\n        background-color: {CURRENT_LINE};\n        color: {PURPLE};\n        border-radius: 8px;\n    }}\n    QListWidget::item:hover {{\n        background-color: {CURRENT_LINE};\n        color: {FOREGROUND};\n    }}\n\n    \n    QLabel {{\n        color: {FOREGROUND};\n        font-weight: bold;\n        margin-bottom: 5px;\n    }}\n    QLineEdit, QPlainTextEdit {{\n        background-color: {CURRENT_LINE};\n        color: {FOREGROUND};\n        border: 1px solid transparent;\n        border-radius: {BORDER_RADIUS};\n        padding: 12px;\n        font-size: {INPUT_FONT_SIZE};\n        font-size: {INPUT_FONT_SIZE};\n        height: {INPUT_HEIGHT};\n        margin-bottom: 5px;\n        selection-background-color: {PURPLE};\n    }}\n    QLineEdit:focus, QPlainTextEdit:focus {{\n        border: 1px solid {PURPLE};\n    }}\n    \n    \n    QLineEdit#iconInput {{\n        padding-left: 45px;\n        background-repeat: no-repeat;\n        background-position: left 12px center;\n    }}\n\n    \n    QPushButton {{\n        background-color: {CURRENT_LINE};\n        color: {FOREGROUND};\n        border: none;\n        border-radius: {BORDER_RADIUS};\n        padding: 10px 20px;\n        font-weight: bold;\n        height: {INPUT_HEIGHT};\n    }}\n    QPushButton:hover {{\n        background-color: {COMMENT};\n    }}\n    QPushButton:pressed {{\n        background-color: {PURPLE};\n        color: {BACKGROUND};\n    }}\n    \n    \n    QPushButton#actionBtn {{\n        background-color: {GREEN};\n        color: {BACKGROUND};\n        font-size: {BTN_GENERATE_FONT_SIZE};\n        font-weight: bold;\n        height: {BTN_GENERATE_HEIGHT};\n        border-radius: {BORDER_RADIUS};\n    }}\n    QPushButton#actionBtn:hover {{\n        background-color: #69ff94; \n    }}\n    \n    \n    QPushButton#secondaryBtn {{\n        background-color: {PURPLE};\n        color: {BACKGROUND};\n        font-size: 14px;\n        border-radius: {BORDER_RADIUS};\n        height: {BTN_ACTION_HEIGHT};\n    }}\n    QPushButton#secondaryBtn:hover {{\n        background-color: #d6acff;\n    }}\n\n    \n    QComboBox {{\n        background-color: {CURRENT_LINE};\n        color: {FOREGROUND};\n        border: none;\n        border-radius: 12px;\n        padding: 10px;\n        min-width: 60px;\n        height: {INPUT_HEIGHT};\n    }}\n    QComboBox::drop-down {{\n        border: none;\n    }}\n    QComboBox QAbstractItemView {{\n        background-color: {CURRENT_LINE};\n        color: {FOREGROUND};\n        selection-background-color: {PURPLE};\n        border: 1px solid {COMMENT};\n    }}\n\n    \n    QSlider::groove:horizontal {{\n        border: 1px solid {COMMENT};\n        height: 6px;\n        background: {CURRENT_LINE};\n        margin: 2px 0;\n        border-radius: 3px;\n    }}\n    QSlider::sub-page:horizontal {{\n        background: {PURPLE};\n        border-radius: 3px;\n    }}\n    QSlider::handle:horizontal {{\n        background: {CYAN};\n        border: 1px solid {CYAN};\n        width: 16px;\n        height: 16px;\n        margin: -6px 0;\n        border-radius: 8px;\n    }}\n\n    \n    QGroupBox {{\n        border: 1px solid {COMMENT};\n        border-radius: {BORDER_RADIUS};\n        font-weight: bold;\n        font-size: 12px; \n        padding-top: 15px;\n        margin-top: 15px; \n    }}\n    QGroupBox::title {{\n        subcontrol-origin: margin;\n        subcontrol-position: top center;\n        padding: 0 10px;\n        color: {PURPLE};\n    }}\n    \n    \n    QCheckBox {{\n        color: {FOREGROUND};\n        spacing: 8px;\n    }}\n    QCheckBox::indicator {{\n        width: 18px;\n        height: 18px;\n        border: 1px solid {COMMENT};\n        border-radius: 4px;\n        background: {BACKGROUND};\n    }}\n    QCheckBox::indicator:checked {{\n        background: {PURPLE};\n        border: 1px solid {PURPLE};\n    }}\n    \n    \n    QScrollArea {{\n        border: none;\n        background-color: transparent;\n        background: transparent;\n    }}\n    QScrollArea > QWidget > QWidget {{\n        background: transparent;\n        padding-right: 10px;\n    }}\n    QScrollBar:vertical {{\n        border: none;\n        background: {BACKGROUND};\n        width: 10px;\n        margin: 0px 0px 0px 0px;\n    }}\n    QScrollBar::handle:vertical {{\n        background: {COMMENT};\n        min-height: 20px;\n        border-radius: 5px;\n    }}\n    QScrollBar::add-line:vertical, QScrollBar::sub-line:vertical {{\n        border: none;\n        background: none;\n    }}\n    QScrollBar::add-page:vertical, QScrollBar::sub-page:vertical {{\n        background: none;\n    }}\n    \n    \n    QScrollBar:horizontal {{\n        border: none;\n        background: {BACKGROUND};\n        height: 10px;\n        margin: 0px 0px 0px 0px;\n    }}\n    QScrollBar::handle:horizontal {{\n        background: {COMMENT};\n        min-width: 20px;\n        border-radius: 5px;\n    }}\n    QScrollBar::add-line:horizontal, QScrollBar::sub-line:horizontal {{\n        border: none;\n        background: none;\n    }}\n    QScrollBar::add-page:horizontal, QScrollBar::sub-page:horizontal {{\n        background: none;\n    }}\n\n    \n    PreviewLabel {{\n        background-color: #FFFFFF;\n        border-radius: 12px;\n    }}\n    "